mapper.select:
		select *
		from(
		SELECT *
		FROM SUBSCRIBE S
		LEFT JOIN ITEM I ON(S.ITEM_NO=I.ITEM_NO)
		LEFT JOIN DELIVERY D ON(S.SUBSCRIBE_NO=D.SUBSCRIBE_NO)
		LEFT JOIN PAYMENT P ON(S.SUBSCRIBE_NO=P.SUBSCRIBE_NO)
		
			
				DELIVERY_STATUS=#{category1}
			

		
		ORDER BY SUBSCRIBE_DATE DESC
		)
		
			
				subscribe_no=#{subscribeNo}
			
			
				or item_name=#{itemName}
			
			
				or member_id=#{memberId}

