mapper.select:
SELECT TO_CHAR(PAYMENT_DATE,'yyyy-mm-dd') AS PAYMENT_DATE,NVL(SUM(ITEM_PRICE*AMOUNT),0) AS TOTAL_PAYMENT,COUNT(*) AS TOTAL_COUNT FROM PAYMENT P LEFT JOIN ITEM I ON(P.ITEM_NO=I.ITEM_NO) GROUP BY TO_CHAR(PAYMENT_DATE,'yyyy-mm-dd') HAVING TO_CHAR(PAYMENT_DATE,'yyyy-mm-dd') IN
#{i}
ORDER BY PAYMENT_DATE DESC

SELECT COUNT(*) FROM( SELECT TO_CHAR(PAYMENT_DATE,'yyyy') AS PAYMENT_DATE,NVL(SUM(ITEM_PRICE*AMOUNT),0) AS TOTAL_PAYMENT,COUNT(*) AS TOTAL_COUNT FROM PAYMENT P LEFT JOIN ITEM I ON(P.ITEM_NO=I.ITEM_NO) GROUP BY TO_CHAR(PAYMENT_DATE,'yyyy') HAVING
TO_CHAR(PAYMENT_DATE,'yyyy') LIKE #{i}
ORDER BY PAYMENT_DATE DESC)

SELECT TO_DATE(TO_CHAR(PAYMENT_DATE,'yyyy'),'yyyy') AS PAYMENT_DATE,NVL(SUM(ITEM_PRICE*AMOUNT),0) AS TOTAL_PAYMENT,COUNT(*) AS TOTAL_COUNT FROM PAYMENT P LEFT JOIN ITEM I ON(P.ITEM_NO=I.ITEM_NO) GROUP BY TO_CHAR(PAYMENT_DATE,'yyyy') HAVING
TO_CHAR(PAYMENT_DATE,'yyyy') LIKE #{i}
ORDER BY PAYMENT_DATE DESC

SELECT COUNT(*) FROM( SELECT TO_CHAR(PAYMENT_DATE,'yyyy-MM') AS PAYMENT_DATE,NVL(SUM(ITEM_PRICE*AMOUNT),0) AS TOTAL_PAYMENT,COUNT(*) AS TOTAL_COUNT FROM PAYMENT P LEFT JOIN ITEM I ON(P.ITEM_NO=I.ITEM_NO) GROUP BY TO_CHAR(PAYMENT_DATE,'yyyy-MM') HAVING
TO_CHAR(PAYMENT_DATE,'yyyy-MM') LIKE #{i}
ORDER BY PAYMENT_DATE DESC)

SELECT TO_DATE(TO_CHAR(PAYMENT_DATE,'yyyy-MM'),'yyyy-MM') AS PAYMENT_DATE,NVL(SUM(ITEM_PRICE*AMOUNT),0) AS TOTAL_PAYMENT,COUNT(*) AS TOTAL_COUNT FROM PAYMENT P LEFT JOIN ITEM I ON(P.ITEM_NO=I.ITEM_NO) GROUP BY TO_CHAR(PAYMENT_DATE,'yyyy-MM') HAVING
TO_CHAR(PAYMENT_DATE,'yyyy-MM') LIKE #{i}
ORDER BY PAYMENT_DATE DESC

		SELECT CATEGORY_NO,NVL(SUM(ITEM_PRICE*AMOUNT),0) AS
		TOTAL_PAYMENT,COUNT(*) AS TOTAL_COUNT
		FROM(
		SELECT
		I.*,P.PAYMENT_NO,P.AMOUNT,P.POINT,P.SUBSCRIBE_NO,P.PAYMENT_DATE,P.MEMBER_NO,P.MEMBER_ID,P.EMAIL
		FROM PAYMENT P
		LEFT JOIN ITEM I ON (I.ITEM_NO=P.ITEM_NO)
		WHERE P.PAYMENT_DATE >= #{startDay}
		AND P.PAYMENT_DATE  <= #{lastDay}
		AND CATEGORY_NO=#{category1}

		)
		GROUP BY CATEGORY_NO

		select count(*)
		from (
		SELECT *
		FROM payment p
		left join item i on(i.item_no=p.item_no)
		where

		p.PAYMENT_DATE >= 
		#{startDay}

		and p.PAYMENT_DATE <
		#{lastDay}
		)

		
			
				or subscribe_no = #{subscribeNo}
			
			
				or item_no = #{itemNo}
			
			
				or item_name = #{itemName}
			
			
				or member_id = #{memberId}
			
		
		order by payment_date desc

		select *
		from(
		SELECT
		I.*,P.PAYMENT_DATE,P.PAYMENT_NO,P.AMOUNT,P.POINT,P.SUBSCRIBE_NO,P.MEMBER_NO,P.MEMBER_ID,P.EMAIL,(i.ITEM_PRICE*p.AMOUNT-p.POINT)
		AS TOTAL_PAYMENT
		FROM PAYMENT P
		LEFT JOIN ITEM I ON (I.ITEM_NO=P.ITEM_NO)
		WHERE P.PAYMENT_DATE >= 
		to_date(#{startDay},'yyyy-mm-dd') AND P.PAYMENT_DATE <
		to_date(#{lastDay},'yyyy-mm-dd')
		
			
				CATEGORY_NO=#{categoryNo}
			
		
		order by p.payment_date
		)
		

			
				or subscribe_no = #{subscribeNo}
			
			
				or item_no = #{itemNo}
			
			
				or item_name = #{itemName}
			
			
				or member_id = #{memberId}
			
		
		order by payment_date desc

